sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    try {
        var cityName = request.pathParams.city;

        if (!cityName) {
            response.setStatus(400);
            response.setBody({
                error: 'City parameter is required in the path',
            });
            return;
        }

        var grWeather = new GlideRecord('x_1117051_cityweat_cityweatherinfo');
        grWeather.addQuery('city', cityName);
        grWeather.query();

        if (!grWeather.next()) {
            response.setStatus(400);
            response.setBody({
                error: "City not found: " + cityName
            });
            return;
        }

        grWeather.deleteRecord();
        var result = {
            message: 'City weather record deleted sucessfully',
            city: cityName,
        };

        response.setStatus(200);
        response.setBody(result);

    } catch (error) {
        response.setStatus(500);
        response.setBody({
            error: "An error occured while processing your request",
            details: error.details,
        });
    }
})(request, response);
