sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var results = [];

	//Retrive params from the request
	var city = request.queryParams.city;
	var condition = request.queryParams.condition;
	var humidity = request.queryParams.humidity;
	var pressure = request.queryParams.pressure;
	var temperature = request.queryParams.temperature;

	//Validate query parameters

	if (!city && !condition && !humidity && !pressure && !temperature) {
		response.setStatus(400);
		response.setBody({error: 'No query parameters provided. Please provide city, condition, humidity, pressure or temperature.'});
		return;
	}

	//Initialize glideRecord for the CityWeatherInfo
	var grWeather = new GlideRecord('x_1117051_cityweat_cityweatherinfo');

	//Add query based on params provided
	if (city) {
		grWeather.addQuery(city);
	}

	if (condition) {
		grWeather.addQuery(condition);
	}

	if (humidity) {
		grWeather.addQuery(humidity);
	}

	if (pressure) {
		grWeather.addQuery(pressure);
	}

	if (temperature) {
		grWeather.addQuery(temperature);
	}

	grWeather.query();

	//Process the results and build the response
	while (grWeather.next()) {
		results.push({
			number: grWeather.getValue('number'),
			city: grWeather.getValue('city'),
			condition: grWeather.getValue('condition'),
			humidity: grWeather.getValue('humidity'),
			pressure: grWeather.getValue('pressure'),
			temperature: grWeather.getValue('temperature'),
			link: {
				self: 'https://dev322884.service-now.com/now/nav/ui/classic/params/target/x_1117051_cityweat_cityweatherinfo.do%3Fsys_id=' + grWeather.getUniqueValue()
			}
		});
	}

	//No records found
	if (results.length == 0) {
		response.setStatus(400);
		response.setBody({error: 'No cities found matching the criteria'});
		return;
	}

	//Return the data
	response.setStatus(200);
	response.setBody(results);


})(request, response);
