sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    try {

        // extract the city name from the patch param
        var cityName = request.pathParams.city;
        if (!cityName) {
            response.setStatus(400);
            response.setBody({
                error: "City parameter is required in the path"
            });
            return;
        }

        // Parse the request body for the fields to update
        var requestBody = request.body.data;
        if (!requestBody) {
            response.setStatus(400);
            response.setBody({
                error: "Request body is required. Please provide which field and values to update"
            });
            return;
        }

        //Initialize the GlideRecord for CityWeather table
        var grWeather = new GlideRecord('x_1117051_cityweat_cityweatherinfo');
        grWeather.addQuery('city', cityName);
        grWeather.query();

        // check if exist
        if (!grWeather.next()) {
            response.setStatus(404);
            response.setBody({
                error: "City not found: " + cityName
            });
            return;
        }

        //update the record
        if (requestBody.temperature) {
            grWeather.setValue('temperature', requestBody.temperature);
        }

        if (requestBody.condition) {
            grWeather.setValue('condition', requestBody.condition);
        }

        if (requestBody.humidity) {
            grWeather.setValue('humidity', requestBody.humidity);
        }

        if (requestBody.pressure) {
            grWeather.setValue('pressure', requestBody.pressure);
        }

        grWeather.update();

		// response object

		var result = {
			sys_id: grWeather.getValue('sys_id'),
			number: grWeather.getValue('number'),
			city: grWeather.getValue('city'),
			humidity: grWeather.getValue('humidity'),
			temperature: grWeather.getValue('temperature'),
			condition: grWeather.getValue('condition'),
			pressure: grWeather.getValue('pressure'),
			link: {
				self: 'https://dev322884.service-now.com/now/nav/ui/classic/params/target/x_1117051_cityweat_cityweatherinfo.do%3Fsys_id=' + grWeather.getUniqueValue()
			}
		};

		response.setStatus(200);
		response.setBody(result);


    } catch (error) {
        response.setStatus(500);
        response.setBody({
            error: 'An error occured while processing your request.',
            details: error.details
        });
    }

})(request, response);
